import { __decorate } from "tslib";
import { Component, ViewChild } from '@angular/core';
let VideoComponent = class VideoComponent {
    constructor(elementRef, faceApiService, videoService) {
        this.elementRef = elementRef;
        this.faceApiService = faceApiService;
        this.videoService = videoService;
        this.modelsReady = false;
        this.listEvents = [];
        this.checkFace = () => {
            setInterval(async () => {
                await this.videoService.getLandMark(this.videoTest);
            }, 100);
        };
    }
    ngOnInit() {
        this.listenerEvents();
    }
    ngOnDestroy() {
        this.listEvents.forEach(event => event.unsubscribe());
    }
    listenerEvents() {
        const observer1$ = this.faceApiService.cbModels.subscribe(res => {
            //TODO
            this.modelsReady = true;
            this.checkFace();
        });
        this.listEvents = [observer1$];
    }
    playVideo() {
        console.log("************ Play Video ************");
        const myVideo = document.getElementById("videoTest");
        if (myVideo.paused) {
            myVideo.play();
        }
        else {
            myVideo.pause();
        }
    }
};
